list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX foaf: <http://xmlns.com/foaf/0.1/> PREFIX fn: <http://www.w3.org/2005/xpath-functions#>
SELECT DISTINCT ?subclass ?position ?positionTitle ?rank ?person ?personName ?dateTimeStart ?dateTimeEnd WHERE { ?subject ?property ?position OPTIONAL { ?position core:relates ?person . ?person a foaf:Person . ?person rdfs:label ?personName . } OPTIONAL { ?position rdfs:label ?positionTitle } OPTIONAL { ?position core:rank ?rank . } OPTIONAL { ?position core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart } OPTIONAL { ?position core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd }
OPTIONAL { ?position vitro:mostSpecificType ?subclass . ?subclass rdfs:subClassOf core:Position . }
} ORDER BY ?subclass (fn:lower-case(?personName))

list-view-config.query-construct:
PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX foaf: <http://xmlns.com/foaf/0.1/> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX obo: <http://purl.obolibrary.org/obo/> PREFIX fhd: <http://vivo.fredhutch.org/ontology/display#>
CONSTRUCT { ?subject ?property ?position . ?position a core:Position . ?position vitro:mostSpecificType ?subclass . ?subclass rdfs:subClassOf core:Position . ?position rdfs:label ?positionTitle . ?position core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart . ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd . ?position core:relates ?person . ?person a foaf:Person . ?person rdfs:label ?personName . ?position core:rank ?rank . } WHERE {
{ ?subject ?property ?position . ?position a core:Position . ?position vitro:mostSpecificType ?subclass . ?position rdfs:label ?positionTitle . ?position core:relates ?person . ?person a foaf:Person . ?person rdfs:label ?personName . OPTIONAL { ?position core:rank ?rank } OPTIONAL { ?position core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart . } OPTIONAL { ?position core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd . } }
UNION { ?subject obo:BFO_0000051 ?childOrg . ?childOrg ?property ?position . ?position a core:Position . ?position vitro:mostSpecificType ?subclass . ?position rdfs:label ?positionTitle . ?position core:relates ?person . ?person a foaf:Person . ?person rdfs:label ?personName . OPTIONAL { ?position core:rank ?rank } OPTIONAL { ?position core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart . } OPTIONAL { ?position core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd . } }
UNION { ?subject obo:BFO_0000051 ?childOrg . ?childOrg obo:BFO_0000051 ?grandChildOrg . ?grandChildOrg ?property ?position . ?position a core:Position . ?position vitro:mostSpecificType ?subclass . ?position rdfs:label ?positionTitle . ?position core:relates ?person . ?person a foaf:Person . ?person rdfs:label ?personName . OPTIONAL { ?position core:rank ?rank } OPTIONAL { ?position core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart . } OPTIONAL { ?position core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd . } } FILTER (?subclass NOT IN (fhd:ProgramLeadership, fhd:Membership, fhd:Clinician, fhd:FormerPosition)) }
