list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX obo: <http://purl.obolibrary.org/obo/> PREFIX fhd: <http://vivo.fredhutch.org/ontology/display#> PREFIX fhs: <http://vivo.fredhutch.org/ontology/service#> PREFIX bibo: <http://purl.org/ontology/bibo/>
SELECT DISTINCT ?subclass ?service ?serviceName ?org ?orgName ?parentOrg ?parentOrgName ?journal ?journalName ?dateTimeStart ?dateTimeEnd WHERE { ?subject ?property ?service . ?service a fhs:Service . ?service rdfs:label ?serviceName . OPTIONAL { ?service fhs:org ?org . ?org rdfs:label ?orgName . } OPTIONAL { ?service fhs:org ?org . ?org fhs:parent ?parentOrg . ?parentOrg rdfs:label ?parentOrgName . } OPTIONAL { ?service fhs:journal ?journal . ?journal rdfs:label ?journalName . } OPTIONAL { ?service core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart . } OPTIONAL { ?service core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd . }
?service vitro:mostSpecificType ?subclass .
} ORDER BY ?subclass DESC(?dateTimeStart)
